import React, { useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const SalesChart = () => {
  const data = [
    { month: 'Jan', primary: 2000, secondary: 1500, total: 3500 },
    { month: 'Feb', primary: 3000, secondary: 1200, total: 4200 },
    { month: 'Mar', primary: 1000, secondary: 500, total: 1500 },
    { month: 'Apr', primary: 3500, secondary: 2500, total: 6000 },
    { month: 'May', primary: 2500, secondary: 1500, total: 4000 },
    { month: 'Jun', primary: 1000, secondary: 800, total: 1800 },
    { month: 'Jul', primary: 4000, secondary: 2000, total: 6000 },
    { month: 'Aug', primary: 2800, secondary: 1200, total: 4000 },
    { month: 'Sep', primary: 3000, secondary: 1800, total: 4800 },
    { month: 'Oct', primary: 2600, secondary: 900, total: 3500 },
    { month: 'Nov', primary: 2900, secondary: 1000, total: 3900 },
    { month: 'Dec', primary: 3500, secondary: 1500, total: 5000 },
  ];

  const maxValue = 10000;
  const chartHeight = 300;
  const yAxisValues = [10000, 8000, 6000, 4000, 2000, 0];

  const formatCurrency = (value) => `$${value.toLocaleString()}`;

  useEffect(() => {
    const tooltips = document.querySelectorAll('[data-bs-toggle="tooltip"]');
    [...tooltips].forEach(el => new window.bootstrap.Tooltip(el));
  }, []);

  return (
    <div className="bg-white rounded p-4 shadow-sm">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5 className="mb-0">Sales Reports</h5>
        <button className="btn btn-sm btn-outline-secondary">Monthly</button>
      </div>

      <div className="row">
        {/* Y-axis */}
        <div className="col-auto pe-3 d-flex flex-column justify-content-between position-relative" style={{ height: chartHeight }}>
          {yAxisValues.map((val, i) => (
            <div key={val} className="d-flex align-items-center" style={{ height: chartHeight / (yAxisValues.length - 1), borderBottom: "1px solid #e5e7eb", width: "100%" }}>
              <span className="small text-muted" style={{ minWidth: "60px" }}>{formatCurrency(val)}</span>
            </div>
          ))}
        </div>

        {/* Chart area */}
        <div className="col ps-0">
          <div className="d-flex align-items-end justify-between" style={{ height: chartHeight }}>
            {data.map((item) => {
              const primaryHeight = (item.primary / maxValue) * chartHeight;
              const secondaryHeight = (item.secondary / maxValue) * chartHeight;

              return (
                <div key={item.month} className="d-flex flex-column align-items-center mx-1">
                  <div
                    className="position-relative"
                    style={{ height: chartHeight, width: "24px" }}
                    data-bs-toggle="tooltip"
                    title={formatCurrency(item.total)}
                  >
                    {/* Secondary (background) */}
                    <div
                      className="position-absolute bottom-0 w-100 rounded-top"
                      style={{
                        height: `${secondaryHeight}px`,
                        background: "linear-gradient(to top, #EDE9FE, #DDD6FE)",
                      }}
                    />
                    {/* Primary (foreground) */}
                    <div
                      className="position-absolute bottom-0 w-100 rounded-top"
                      style={{
                        height: `${primaryHeight}px`,
                        background: "linear-gradient(to top, #8B5CF6, #A855F7)",
                      }}
                    />
                  </div>
                  <div className="mt-2 small text-muted">{item.month}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SalesChart;
