import React from 'react';

const SalesReport = () => {
  const data = [
    { month: 'Jan', value: 6000 },
    { month: 'Feb', value: 4500 },
    { month: 'Mar', value: 7200 },
    { month: 'Apr', value: 8100 },
    { month: 'May', value: 5800 },
    { month: 'Jun', value: 6900 },
    { month: 'Jul', value: 7800 },
    { month: 'Aug', value: 9200 },
    { month: 'Sep', value: 8500 },
    { month: 'Oct', value: 7100 },
    { month: 'Nov', value: 8800 },
    { month: 'Dec', value: 9500 },
  ];

  const max = 10000;

  return (
    <div className="card p-4 shadow-sm">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5 className="fw-semibold mb-0">Sales Reports</h5>
        <select className="form-select form-select-sm" style={{ width: '100px' }}>
          <option>Monthly</option>
        </select>
      </div>

      <div className="d-flex" style={{ height: '280px' }}>
        {/* Y-axis */}
        <div className="d-flex flex-column justify-content-between text-end pe-2" style={{ width: '60px' }}>
          {[10000, 8000, 6000, 4000, 2000, 0].map((val, i) => (
            <small key={i} className="text-secondary">${val}</small>
          ))}
        </div>

        {/* Bars */}
        <div className="d-flex flex-grow-1 align-items-end ps-2 gap-2 w-100 border-start">
          {data.map((item, index) => {
            const height = `${(item.value / max) * 100}%`;

            return (
              <div key={index} className="text-center d-flex flex-column align-items-center" style={{ flex: 1 }}>
                <div
                  style={{
                    height: height,
                    width: '20px',
                    background: 'linear-gradient(to top, #8B5CF6, #DDD6FE)',
                    borderRadius: '10px 10px 0 0',
                  }}
                  title={`$${item.value.toLocaleString()}`}
                />
                <small className="text-muted mt-1">{item.month}</small>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SalesReport;
