import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const SalesChart = () => {
  const data = [
    { month: 'Jan', primary: 2000, secondary: 1500, total: 3500 },
    { month: 'Feb', primary: 3000, secondary: 1200, total: 4200 },
    { month: 'Mar', primary: 1000, secondary: 500, total: 1500 },
    { month: 'Apr', primary: 3500, secondary: 2500, total: 6000 },
    { month: 'May', primary: 2500, secondary: 1500, total: 4000 },
    { month: 'Jun', primary: 1000, secondary: 800, total: 1800 },
    { month: 'Jul', primary: 4000, secondary: 2000, total: 6000 },
    { month: 'Aug', primary: 2800, secondary: 1200, total: 4000 },
    { month: 'Sep', primary: 3000, secondary: 1800, total: 4800 },
    { month: 'Oct', primary: 2600, secondary: 900, total: 3500 },
    { month: 'Nov', primary: 2900, secondary: 1000, total: 3900 },
    { month: 'Dec', primary: 3500, secondary: 1500, total: 5000 },
  ];

  const maxValue = Math.max(...data.map((d) => d.total));
  const chartHeight = 300;
  const yAxisValues = [10000, 5000, 1000, 500, 200, 0];

  const formatCurrency = (value) => {
    if (value >= 1000) return `$${value / 1000}k`;
    return `$${value}`;
  };



  return (
    <div className="container my-5">
      <div className="row">
        {/* Y-axis */}
        <div className="col-auto d-flex flex-column justify-content-between pe-3" style={{ height: chartHeight }}>
          {yAxisValues.map((val) => (
            <div key={val} className="text-end small text-muted" style={{ height: chartHeight / yAxisValues.length }}>
              {formatCurrency(val)}
            </div>
          ))}
        </div>

        {/* Chart Area */}
        <div className="col">
          <div className="d-flex align-items-end justify-content-between" style={{ height: chartHeight }}>
            {data.map((item) => {
              const primaryHeight = (item.primary / maxValue) * chartHeight;
              const secondaryHeight = (item.secondary / maxValue) * chartHeight;

              return (
                <div key={item.month} className="d-flex flex-column align-items-center px-1">
                  {/* Bar */}
                  <div
                    className="position-relative bar-with-tooltip"
                    style={{ height: chartHeight, width: "24px" }}
                  >
                    {/* Secondary (background) */}
                    <div
                      className="bg-light position-absolute bottom-0 w-100 rounded-top"
                      style={{ height: `${secondaryHeight}px`, background: "linear-gradient(to top, #EDE9FE, #DDD6FE)" }}
                    ></div>
                    {/* Primary (foreground) */}
                    <div
                      className="bg-primary position-absolute bottom-0 w-100 rounded-top"
                      style={{ height: `${primaryHeight}px`, background: "linear-gradient(to top, #8B5CF6, #A855F7)" }}
                    ></div>
                    {/* Custom tooltip */}
                    <div className="custom-tooltip">
                      {formatCurrency(item.total)}
                    </div>
                  </div>
                  {/* Label */}
                  <div className="mt-2 small text-muted">{item.month}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

// Add custom tooltip CSS
const style = document.createElement('style');
style.innerHTML = `
.bar-with-tooltip {
  position: relative;
}
.bar-with-tooltip .custom-tooltip {
  visibility: hidden;
  opacity: 0;
  min-width: 60px;
  background: #333;
  color: #fff;
  text-align: center;
  border-radius: 4px;
  padding: 4px 8px;
  position: absolute;
  left: 50%;
  top: -35px;
  transform: translateX(-50%);
  z-index: 10;
  font-size: 12px;
  transition: opacity 0.2s;
  pointer-events: none;
  white-space: nowrap;
}
.bar-with-tooltip:hover .custom-tooltip {
  visibility: visible;
  opacity: 1;
}
`;
if (!document.head.querySelector('style[data-saleschart-tooltip]')) {
  style.setAttribute('data-saleschart-tooltip', 'true');
  document.head.appendChild(style);
}

export default SalesChart;
