import React, { useState } from "react";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import MetricCard from "../features/dashboard/MetricCard";
import ConversionFunnel from "../features/dashboard/ConversionFunnel";
import SalesChart from "../features/dashboard/SalesChart";
import TeamPerformance from "../features/dashboard/TeamPerformance";
import "bootstrap-icons/font/bootstrap-icons.css";

export default function Dashboard() {
  const [activeItem, setActiveItem] = useState("dashboard");

  const metrics = [
  {
    title: "Total Leads",
    value: "1,250",
    icon: "bi-people-fill",             // 游논 purple icon
    bgColor: "bg-primary bg-opacity-10",
    iconColor: "text-primary",
  },
  {
    title: "Active Deals",
    value: "136",
    icon: "bi-briefcase-fill",          // 游눺 greenish icon
    bgColor: "bg-info bg-opacity-10",
    iconColor: "text-info",
  },
  {
    title: "Closed Deals",
    value: "136",
    icon: "bi-briefcase-fill",          // 游눺 reddish icon
    bgColor: "bg-danger bg-opacity-10",
    iconColor: "text-danger",
  },
  {
    title: "Monthly Revenue",
    value: "45,000",
    icon: "bi-wallet-fill",             // 游리 wallet icon (changed from "grid" to match screenshot)
    bgColor: "bg-warning bg-opacity-10",
    iconColor: "text-warning",
  },
];


  // Sample sales data for the chart
  const salesData = [
    { month: "Jan", primary: 1200, secondary: 800, total: 2000 },
    { month: "Feb", primary: 900, secondary: 600, total: 1500 },
    { month: "Mar", primary: 1700, secondary: 1100, total: 2800 },
    { month: "Apr", primary: 1500, secondary: 900, total: 2400 },
    { month: "May", primary: 2000, secondary: 1200, total: 3200 },
    { month: "Jun", primary: 1800, secondary: 1000, total: 2800 },
  ];

  return (
    <div className="min-vh-100 bg-light">
      <Header />
      <div className="d-flex">
        <Sidebar activeItem={activeItem} setActiveItem={setActiveItem} />
        <main className="flex-fill p-4">
          {/* Metric Cards */}
          <div className="row g-4 mb-4">
            {metrics.map((metric, index) => (
              <div key={index} className="col-12 col-md-6 col-lg-3">
                <MetricCard
                  title={metric.title}
                  value={metric.value}
                  icon={metric.icon}
                  bgColor={metric.bgColor}
                  iconColor={metric.iconColor}
                />
              </div>
            ))}
          </div>

          <div className="row g-4 mb-4">
  <div className="col-12 col-lg-4">
    <ConversionFunnel />
  </div>
  <div className="col-12 col-lg-8">
    <SalesChart data={salesData} />
  </div>
</div>

          {/* Performance Table */}
          <TeamPerformance />
        </main>
      </div>
    </div>
  );
}
