import React from "react";

export default function SalesChart() {
  const data = [
    { label: "Jan", value: 1200 },
    { label: "Feb", value: 5200 },
    { label: "Mar", value: 2500 },
    { label: "Apr", value: 4800 },
    { label: "May", value: 9200 },
    { label: "Jun", value: 3100 },
    { label: "Jul", value: 3500 },
    { label: "Aug", value: 8700 },
    { label: "Sep", value: 6500 },
    { label: "Oct", value: 4300 },
    { label: "Nov", value: 5000 },
    { label: "Dec", value: 3900 },
  ];

  const max = 10000;
  const yAxisLabels = [10000, 5000, 1000, 500, 200, 0];

  return (
    <div className="card shadow-sm h-100">
      {/* Header */}
      <div className="card-header bg-white border-0 d-flex justify-content-between align-items-center">
        <h6 className="mb-0 fw-bold text-dark">Sales Reports</h6>
        <select
          className="form-select form-select-sm"
          style={{ width: "120px" }}
        >
          <option>Monthly</option>
          <option>Quarterly</option>
          <option>Yearly</option>
        </select>
      </div>

      {/* Chart Body */}
      <div className="card-body">
        <div className="d-flex" style={{ height: "360px", paddingTop: "10px" }}>
          {/* Y-Axis */}
          <div
            className="d-flex flex-column align-items-end pe-3"
            style={{
              width: "60px",
              height: "100%",
              display: "flex",
              justifyContent: "space-between",
              gap: "10px", 
            }}
          >
            {yAxisLabels.map((val, i) => (
              <div key={i} style={{ minHeight: "20px" }}>
                <small className="text-muted">${val}</small>
              </div>
            ))}
          </div>

          {/* Bar Chart */}
          <div
            className="flex-grow-1 d-flex align-items-end justify-content-between ps-2"
            style={{
              borderLeft: "1px solid #dee2e6",
              height: "100%",
              gap: "6px",
            }}
          >
            {data.map((bar, index) => {
              const heightPercent = (bar.value / max) * 100;

              return (
                <div
                  key={index}
                  className="text-center"
                  style={{ width: "6%" }}
                >
                  <div
                    style={{
                      height: `${heightPercent}%`,
                      background:
                        "linear-gradient(to top, #99ccff, rgba(153, 204, 255, 0.2))",
                      borderRadius: "6px",
                      minHeight: "150px",
                      transition: "height 0.4s ease",
                    }}
                  ></div>
                  <small className="text-muted d-block mt-1">{bar.label}</small>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
