import React from 'react';

const SalesChart = ({ data = [] }) => {
  const chartHeight = 300;
  if (!Array.isArray(data) || data.length === 0) {
    return <div className="w-full text-center py-8 text-gray-500">No sales data available.</div>;
  }
  const maxValue = Math.max(...data.map(d => d.total));

  const formatCurrency = (value) => {
    if (value >= 1000) {
      return `$${value / 1000}k`;
    }
    return `$${value}`;
  };

  const yAxisValues = [0, 200, 500, 1000, 5000, 10000];

  return (
    <div className="w-full">
      <div className="flex items-end justify-between" style={{ height: chartHeight + 40 }}>
        {/* Y-axis */}
        <div className="flex flex-col justify-between h-full pr-4 text-sm text-gray-500">
          {yAxisValues.reverse().map((value) => (
            <div key={value} className="text-right">
              {formatCurrency(value)}
            </div>
          ))}
        </div>

        {/* Chart area */}
        <div className="flex-1 flex items-end justify-between px-4" style={{ height: chartHeight }}>
          {data.map((item, index) => {
            const primaryHeight = (item.primary / maxValue) * chartHeight;
            const secondaryHeight = (item.secondary / maxValue) * chartHeight;
            const totalHeight = (item.total / maxValue) * chartHeight;

            return (
              <div key={item.month} className="flex flex-col items-center group">
                <div 
                  className="relative w-8 sm:w-10 md:w-12 flex flex-col justify-end transition-all duration-300 hover:scale-105"
                  style={{ height: chartHeight }}
                >
                  {/* Secondary bar (background) */}
                  <div 
                    className="absolute bottom-0 w-full bg-gradient-to-t from-purple-200 to-purple-100 rounded-t-md transition-all duration-300 group-hover:from-purple-300 group-hover:to-purple-200"
                    style={{ height: `${secondaryHeight}px` }}
                  />
                  {/* Primary bar (foreground) */}
                  <div 
                    className="absolute bottom-0 w-full bg-gradient-to-t from-purple-600 to-purple-400 rounded-t-md transition-all duration-300 group-hover:from-purple-700 group-hover:to-purple-500 shadow-lg"
                    style={{ height: `${primaryHeight}px` }}
                  />

                  {/* Hover tooltip */}
                  <div className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-gray-800 text-white px-2 py-1 rounded text-xs opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap">
                    {formatCurrency(item.total)}
                  </div>
                </div>

                {/* Month label */}
                <div className="mt-3 text-sm text-gray-600 font-medium">
                  {item.month}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SalesChart;
