import React from 'react';

const SalesReport = () => {
  const data = [
    { month: 'Jan', value: 6000 },
    { month: 'Feb', value: 4500 },
    { month: 'Mar', value: 7200 },
    { month: 'Apr', value: 8100 },
    { month: 'May', value: 5800 },
    { month: 'Jun', value: 6900 },
    { month: 'Jul', value: 7800 },
    { month: 'Aug', value: 9200 },
    { month: 'Sep', value: 8500 },
    { month: 'Oct', value: 7100 },
    { month: 'Nov', value: 8800 },
    { month: 'Dec', value: 9500 },
  ];

  const maxValue = Math.max(...data.map(d => d.value));

  return (
    <div className="bg-white rounded-xl p-6 shadow-sm">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-900">Sales Reports</h3>
        <span className="text-sm text-gray-500">Monthly</span>
      </div>

      <div className="h-80 flex flex-col">
        {/* Y-axis labels */}
        <div className="flex-1 flex">
          <div className="w-16 flex flex-col justify-between text-xs text-gray-500 py-2">
            <span>$10000</span>
            <span>$8000</span>
            <span>$6000</span>
            <span>$4000</span>
            <span>$2000</span>
            <span>$0</span>
          </div>

          {/* Chart area */}
          <div className="flex-1 flex items-end justify-between px-2 pb-2">
            {data.map((item, index) => (
              <div key={item.month} className="flex flex-col items-center flex-1 mx-1">
                <div
                  className="w-8 rounded-t-lg hover:opacity-90 transition-all duration-300 cursor-pointer relative group min-h-[4px]"
                  style={{
                    height: `${Math.max((item.value / maxValue) * 280, 8)}px`,
                    background:
                      'linear-gradient(180deg, #EDE9FE 0%, #DDD6FE 20%, #C084FC 40%, #A855F7 70%, #8B5CF6 100%)',
                  }}
                >
                  <div className="absolute -top-10 left-1/2 transform -translate-x-1/2 bg-gray-900 text-white text-xs px-2 py-1 rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap z-10">
                    ${item.value.toLocaleString()}
                  </div>
                </div>
                <span className="text-xs text-gray-500 mt-3 font-medium">{item.month}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SalesReport;
